              :isbn-10                 "1843763311",
              :language                "Englisch",
              :amazon-id               "I2Q2FOTSRBSNHQ",
              :amazon-book-image-front "https://images-na.ssl-images-amazon.com/images/I/51sYEeZHITL._SX315_BO1,204,203,200_.jpg",
              :book-length             "224 Seiten",
